import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DashboardOverview = () => {
  const [overviewStats, setOverviewStats] = useState({
    totalServers: 0,
    pendingUpdates: 0,
    lastUpdate: 'Loading...',
  });

  useEffect(() => {
    const fetchServerCount = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/servers/count');
        setOverviewStats((prev) => ({
          ...prev,
          totalServers: response.data.total,
        }));
      } catch (error) {
        console.error('Failed to fetch server count:', error);
      }
    };

    fetchServerCount();
  }, []);

  return (
    <div className="p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Dashboard Overview</h2>
      <div className="grid grid-cols-3 gap-4">
        <div className="bg-gray-100 p-4 rounded">
          <h3 className="text-lg font-semibold">Total Servers</h3>
          <p className="text-2xl">{overviewStats.totalServers}</p>
        </div>
        <div className="bg-gray-100 p-4 rounded">
          <h3 className="text-lg font-semibold">Pending Updates</h3>
          <p className="text-2xl">{overviewStats.pendingUpdates}</p>
        </div>
        <div className="bg-gray-100 p-4 rounded">
          <h3 className="text-lg font-semibold">Last Update</h3>
          <p className="text-lg">{overviewStats.lastUpdate}</p>
        </div>
      </div>
    </div>
  );
};

export default DashboardOverview;
